import React, {Component} from 'react'
import {connect} from 'react-redux'
import {firestoreConnect} from 'react-redux-firebase'
import {compose} from 'redux'
import {Redirect} from "react-router-dom";
import {signUp} from "../../store/actions/authActions";
import './Financials.css'
import FundList from './FundList'
import CreateFund from "./CreateFund";
import {setFund} from "../../store/actions/fundActions";

class Financials extends Component{
    constructor(props) {
        super(props);
        this.state = { showCreateFund: false };
    }

    toggleCreateFund() {
        this.setState({
            showCreateFund: !this.state.showCreateFund
        })
    }

    handleSet = (e) => {
        e.preventDefault();
        this.props.setFund()
    }

    render() {
        // console.log(this.props);
        // console.log(this.state);
        // console.log('render');
        //f(funds) return <Redirect to= '/signin'/>;
        const funds = this.props.funds;
        // console.log(funds);
        return(
            <div className={"container mt-10"}>
                <button className={"btn green lighten-1"} onClick={this.handleSet}>Set Account</button>
                <div className ="card z-depth-0">
                    {this.state.showCreateFund
                        ?
                        <CreateFund
                            closePopup={this.toggleCreateFund.bind(this)}
                        />
                        :
                        <div className={"container center"}>
                            <button className="btn green lighten-1 center mt-10 mb-10" onClick={this.toggleCreateFund.bind(this)}>New Financial Account</button>
                        </div>
                    }
                </div>
                <div className={"row"}>
                    <div className={"col"}>
                        {this.props.funds == null
                        ?
                            null
                        :
                            <FundList funds = {{funds}}/>
                        }
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) =>{
    // console.log(state);
    // if(typeof state.firestore.ordered.funds == 'undefined'){
    //     console.log('invalid');
    //     return {};
    // }
    // else{
    //     console.log('valid');
    //     return {
    //         funds: state.firestore.ordered.funds
    //     };
    // }
    return {
        funds: state.firestore.ordered.funds
    };
};

const mapDispatchToProps=(dispatch)=>{
    return{
        setFund: (fund)=> dispatch(setFund(fund))
    }
}

export default compose(
    connect(mapStateToProps, mapDispatchToProps),
    firestoreConnect([
        {collection: 'funds'}
    ])
)(Financials)
